import { useState } from 'react'

export default function Form() {
    let [form, setForm] = useState({
        firstName: "",
        lastName: "",
        phoneNumber: '',
        email: "",
        zipCode: "",
        purchasingMask: false,
        sewing: false,
        donatingMaterial: false,
    })

    const handleChange = (event) => {
        const { name, value, type, checked } = event.target
        type === "checkbox" ?
            this.setState({
                [name]: checked
            })
            :
            this.setState({
                [name]: value
            })
    }

    return (
        <div>
            <main>
                <form>
                    <input
                        name="firstName"
                        value={form.firstName}
                        onChange={(e) => handleChange(e)}
                        placeholder="First Name"
                    />
                    <br />

                    <input
                        name="lastName"
                        value={form.lastName}
                        onChange={(e) => handleChange(e)}
                        placeholder="Last Name"
                    />
                    <br />

                    <input
                        name="phoneNumber"
                        value={form.phoneNumber}
                        onChange={(e) => handleChange(e)}
                        placeholder="phone Number"
                    />
                    <br />

                    <input
                        name="email"
                        value={form.email}
                        onChange={(e) => handleChange(e)}
                        placeholder="email"
                    />
                    <br />

                    <input
                        name="zipCode"
                        value={form.zipCode}
                        onChange={(e) => handleChange(e)}
                        placeholder="zip code"
                    />
                    <br />

                    <label>
                        <input
                            type="checkbox"
                            name="purchasingMask"
                            onChange={(e) => handleChange(e)}
                            checked={form.purchasingMask}
                        /> Purchase a mask?
            </label>
                    <br />

                    <label>
                        <input
                            type="checkbox"
                            name="sewing"
                            onChange={(e) => handleChange(e)}
                            checked={form.sewing}
                        /> sewing?
            </label>
                    <br />

                    <label>
                        <input
                            type="checkbox"
                            name="donatingMaterial"
                            onChange={(e) => handleChange(e)}
                            checked={form.donatingMaterial}
                        /> Donating Material?
            </label>
                    <br />

                    <button>Submit</button>
                </form>
                <hr />
            </main>
            <style jsx>{`
            .button {
                background-color: rgb(26,26,26,.7);
                border: none;
                color: white;
                padding: 15px 32px;
                text-align: center;
                text-decoration: none;
                display: inline-block;
                font-size: 16px;
              }
            `}</style>
        </div>

    )
}
